import './App.css';
import React, { useState } from 'react';
import axios from 'axios';
import SearchBox from "../COMP/Searchbox";
import Citytemp from "../COMP/citytemp";
import Infobox from "../COMP/infobox";
import "../COMP/Searchbox.css"




function App() {
  const [data, setData] = useState({});
  const [location, setLocation] = useState('');


  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=1bfcf5e99382f02e60ef6d436939525c&units=metric&lang=ar`;


  const searchLocation = (event) => {
    if (event.key == 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data);
        console.log(response.data);
      });
    }

  }

    return (
    <div>
      <div id="searchBox">
            <input type="search" placeholder="ادخل المدينة" id="search" value={location} onChange={event => setData(event.target.value)}  onKeyPress={searchLocation} />
      </div>
      <Citytemp />
      <Infobox />
   </div>
    );

}

export default App;
