import './App.css';
import React from 'react';
import SearchBox from "../COMP/Searchbox";
import Citytemp from "../COMP/citytemp";
import Infobox from "../COMP/infobox";


class App extends React.Component {
  constructor() {
    super();
    this.state = {
      weather: [],
      location: '',
    }
  }


   url = `https://api.openweathermap.org/data/2.5/weather?q=${this.location}&appid=1bfcf5e99382f02e60ef6d436939525c&units=metric&lang=ar`;

  onsl = (event) => {
    if (event.key === 'Enter') {
      fetch(`https://api.openweathermap.org/data/2.5/weather?q=${this.location}&appid=1bfcf5e99382f02e60ef6d436939525c&units=metric&lang=ar`)
        .then(reponse => reponse.json())
        .then(w => { this.setState({ weather: w }); console.log(w) });
    }
  }
  onchange = (event) => {
    this.setState({ location: event.target.value})
  }


  render(){
    return (
    <div>
      <SearchBox onk={this.onsl} onc={this.onchange} />
      <Citytemp />
      <Infobox />
   </div>
    );
   };

}

export default App;
